import {Http} from '@burst/http';

/**
 * Base API class.
 * Extend and specific APIs here
 */
class BaseApi  {
    private _http: Http;

    /**
     * @returns The internal Http client
     */
    protected get http(): Http {
        return this._http;
    }

    /**
     * Creates API instance
     * @param baseUrl The host url of API
     */
    constructor(baseUrl: string) {
        this._http = new Http(baseUrl);
    }


    // TODO unify/centralize exception handling here!
}

export default BaseApi;

import BaseApi from './baseApi';

interface User {
    name: string;
    url: string;
}

/**
 * Playing around with the guthub API
 */
class Github extends BaseApi {

    constructor() {
        super('https://api.github.com/repos/burst-apps-team');
    }

    public async getAllPhoenixContributors(): Promise<User[]> {
        const httpResponse = await this.http.get('/phoenix/contributors');
        if (httpResponse.hasError()) { throw httpResponse.error; }
        return httpResponse.response.map( c => c.login);
    }
}

export default Github;

