import { Keys } from './keys';
import { Transaction } from './transaction';

/*
* Account class
*
* The account class serves as a model for a Burstcoin account.
* Each account contains its Burst address and numeric id.
*/
export class Account {
    public id: string;
    public address: string;
    public alias: string;
    public balance: number;
    public keys: Keys;
    public pinHash: string;
    public selected: boolean;
    public transactions: Transaction[];
    public type: string;
    public unconfirmedBalance: number;

    constructor(data: any = {}) {
        this.id = data.id || undefined;
        this.address = data.address || undefined;
        this.alias = data.alias || undefined;
        this.balance = data.balance || 0;
        if (data.keys !== undefined) {
            this.keys = new Keys();
            this.pinHash = data.pinHash || undefined;
            this.keys.publicKey = data.keys.publicKey || undefined;
            this.keys.signPrivateKey = data.keys.signPrivateKey || undefined;
            this.keys.agreementPrivateKey = data.keys.agreementPrivateKey || undefined;
        }
        this.selected = data.selected || false;
        if (data.transactions !== undefined && data.transactions.length > 0) {
            this.transactions = data.transactions;
        } else {
            this.transactions = [];
        }
        this.type = data.type || 'offline';
        this.unconfirmedBalance = data.unconfirmedBalance || 0;
    }
}

/*
* Copyright 2018 PoC-Consortium
*/

export interface Attachment {
    message?: string;
    encryptedMessage?: string;
}

/*
* Attachment class
*
* The attachment class is used to appended to transaction where appropriate.
* It is a super class for Message and EncryptedMessage.
*/
export class Attachment {
    public type?: string;

    constructor(type: string) {
        this.type = type;
    }
}

/*
* Message class
*
* The Message class is used to model a plain message attached to a transaction.
*/
export class Message extends Attachment {
    public messageIsText: boolean;
    public message: string;

    constructor(data: any = {}) {
        super("message")
        this.messageIsText = data.messageIsText || false;
        this.message = data.message || undefined;
    }
}

/*
* EncryptedMessage class
*
* The EncryptedMessage class is a model for a encrypted message attached to a transaction.
*/
export class EncryptedMessage extends Attachment {
    public data: string;
    public nonce: string;
    public isText: boolean;

    constructor(data: any = {}) {
        super("encrypted_message")
        this.data = data.data || undefined;
        this.nonce = data.nonce || undefined;
        this.isText = data.isText || false;
    }
}

