/*
* Verify signature for transaction
* h1 = sha256(sha256(transactionHex)_keygen(sha256(transactionHex_privateKey)).publicKey)
* ==
* sha256(sha256(transactionHex)_verify(v, h1, publickey)) = h2
*/
export const verifySignature = (signature: string, transactionHex: string, publicKey: string): boolean => {
    // get bytes
    let signatureBytes = Converter.convertHexStringToByteArray(signature);
    let publicKeyBytes = Converter.convertHexStringToByteArray(publicKey);
    let v = signatureBytes.slice(0, 32);
    let h1 = signatureBytes.slice(32);
    let y = ECKCDSA.verify(v, h1, publicKeyBytes);
    let m = Converter.convertWordArrayToByteArray(CryptoJS.SHA256(CryptoJS.enc.Hex.parse(transactionHex)));
    let m_y = m.concat(y);
    let h2 = Converter.convertWordArrayToByteArray(CryptoJS.SHA256(Converter.convertByteArrayToWordArray(m_y)));
    // Convert to hex
    let h1hex = Converter.convertByteArrayToHexString(h1);
    let h2hex = Converter.convertByteArrayToHexString(h2);
    // compare
    return h1hex == h2hex;
}

import axios from 'axios';
import HttpResponse from './httpResponse';
import HttpError from './httpError';

/**
 * A generic HTTP client
 */
class Http {

    private _clientImpl: any;

    /**
     * Creates your Http client
     * @param baseURL The baseUrl, i.e host url
     */
    constructor(baseURL: string) {
        // see https://github.com/axios/axios#request-config for more
        this._clientImpl = axios.create({
            baseURL,
        });
    }

    static mountError(url: string, error: any): HttpError {
        if (error.response) {
            return new HttpError(url, error.response.status, error.response.statusText, error.response.data);
        } else if (error.request) {
            return new HttpError(url, 0, 'Request failed', error.request);
        }
        return new HttpError(url, -1, 'Http Configuration error', null);
    }

    /**
     * Get Method
     * @param url The relative url
     * @returns request result in case of success or error
     */
    async get(url: string): Promise<HttpResponse> {
        try {
            const {status, data} = await this._clientImpl.get(url);
            return new HttpResponse(status, data);
        } catch (error) {
            throw Http.mountError(url, error);
        }
    }

    /**
     * Post Method
     * @param url The relative url
     * @param payload The post data
     * @returns request result in case of success or error
     */
    async post(url: string, payload: any): Promise<HttpResponse> {
        try {
            const {status, data} = await this._clientImpl.post(url, payload);
            return new HttpResponse(status, data);
        } catch (error) {
            throw Http.mountError(url, error);
        }
    }

    /**
     * Put Method
     * @param url The relative url
     * @param payload The put data
     * @returns request result in case of success or error
     */
    async put(url: string, payload: any): Promise<HttpResponse> {
        try {
            const {status, data} = await this._clientImpl.put(url, payload);
            return new HttpResponse(status, data);
        } catch (error) {
            throw Http.mountError(url, error);
        }
    }

    /**
     * Delete Method
     * @param url The relative url
     * @returns request result in case of success or error
     */
    async delete(url: string) {
        try {
            const {status, data} = await this._clientImpl.delete(url);
            return new HttpResponse(status, data);
        } catch (error) {
            throw Http.mountError(url, error);
        }
    }
}

export default Http;

